import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate, useParams } from 'react-router-dom';
import { Clock, BookOpen, Award, BarChart, Search, Bookmark, Home, User, Menu, X, CheckCircle, XCircle } from 'lucide-react';

// Mock data for questions
const questionBank = {
  'general-knowledge': {
    title: 'General Knowledge',
    topics: [
      {
        id: 1,
        topic: 'Indian History',
        questions: [
          {
            id: 1,
            question: 'Who was the founder of the Mauryan Empire?',
            options: ['Chandragupta Maurya', 'Ashoka', 'Bindusara', 'Samudragupta'],
            correct: 0,
            explanation: 'Chandragupta Maurya founded the Mauryan Empire in 321 BCE.'
          },
          {
            id: 2,
            question: 'The Battle of Plassey was fought in which year?',
            options: ['1757', '1764', '1761', '1756'],
            correct: 0,
            explanation: 'The Battle of Plassey was fought on 23 June 1757.'
          }
        ]
      },
      {
        id: 2,
        topic: 'Kerala History',
        questions: [
          {
            id: 3,
            question: 'Who was known as the "Lion of Kerala"?',
            options: ['Pazhassi Raja', 'Velu Thampi', 'Martanda Varma', 'Marthanda Varma'],
            correct: 0,
            explanation: 'Kerala Varma Pazhassi Raja was known as the "Lion of Kerala".'
          }
        ]
      }
    ]
  },
  'current-affairs': {
    title: 'Current Affairs',
    topics: [
      {
        id: 3,
        topic: 'National Affairs',
        questions: [
          {
            id: 4,
            question: 'What is the capital of Telangana?',
            options: ['Warangal', 'Hyderabad', 'Nizamabad', 'Karimnagar'],
            correct: 1,
            explanation: 'Hyderabad is the capital of Telangana state.'
          }
        ]
      }
    ]
  },
  'english': {
    title: 'English',
    topics: [
      {
        id: 4,
        topic: 'Grammar',
        questions: [
          {
            id: 5,
            question: 'Choose the correct sentence:',
