// Get all questions from question bank
const getAllQuestions = () => {
  const allQuestions = [];
  Object.values(questionBank).forEach(section => {
    section.topics.forEach(topic => {
      topic.questions.forEach(question => {
        allQuestions.push({
          ...question,
          section: section.title,
          topic: topic.topic
        });
      });
    });
  });
  return allQuestions;
};

